import{_ as n,c as r,o as l,ag as s}from"./chunks/framework.CmkU-fWO.js";const o="/cours/assets/mrd-concessionnaire.CBVBi7i7.png",g=JSON.parse('{"title":"Lire un MRD (Modèle relationnel de données)","description":"","frontmatter":{"title":"Lire un MRD (Modèle relationnel de données)","aside":false},"headers":[],"relativePath":"modules/01-introduction/03-modelisation.md","filePath":"modules/01-introduction/03-modelisation.md"}'),t={name:"modules/01-introduction/03-modelisation.md"};function i(a,e,d,c,u,m){return l(),r("div",null,[...e[0]||(e[0]=[s('<h1 id="modele-relationnel-de-donnees-mrd" tabindex="-1">Modèle relationnel de données (MRD) <a class="header-anchor" href="#modele-relationnel-de-donnees-mrd" aria-label="Permalink to &quot;Modèle relationnel de données (MRD)&quot;">​</a></h1><p>Un <strong>MRD</strong> décrit visuellement la <strong>structure</strong> d’une base de données relationnelle : tables, clés et relations.</p><p>L’objectif est de <strong>savoir les lire et les interpréter</strong>, pas de les concevoir à partir de zéro.</p><hr><h2 id="objectif-du-module" tabindex="-1">Objectif du module <a class="header-anchor" href="#objectif-du-module" aria-label="Permalink to &quot;Objectif du module&quot;">​</a></h2><p>Être capable de :</p><ul><li>repérer les <strong>tables</strong>, <strong>PK</strong> et <strong>FK</strong></li><li>déduire les <strong>relations</strong> (1–N, N–N)</li><li>comprendre <strong>où sont stockées les informations</strong></li></ul><hr><h2 id="rappels-essentiels" tabindex="-1">Rappels essentiels <a class="header-anchor" href="#rappels-essentiels" aria-label="Permalink to &quot;Rappels essentiels&quot;">​</a></h2><hr><h3 id="cles-a-reperer-en-premier" tabindex="-1">Clés (à repérer en premier) <a class="header-anchor" href="#cles-a-reperer-en-premier" aria-label="Permalink to &quot;Clés (à repérer en premier)&quot;">​</a></h3><ul><li><strong>PK</strong> : identifie une ligne de façon unique</li><li><strong>FK</strong> : relie une table à une autre</li></ul><hr><h3 id="lire-les-relations" tabindex="-1">Lire les relations <a class="header-anchor" href="#lire-les-relations" aria-label="Permalink to &quot;Lire les relations&quot;">​</a></h3><ul><li>FK vers une autre table → <strong>relation 1–N</strong></li><li>Table avec <strong>au moins deux FK</strong> → <strong>relation N–N</strong> (table associative)</li></ul><hr><h2 id="exemple-—-mrd-concessionnaire" tabindex="-1">Exemple — MRD Concessionnaire <a class="header-anchor" href="#exemple-—-mrd-concessionnaire" aria-label="Permalink to &quot;Exemple — MRD Concessionnaire&quot;">​</a></h2><p><img src="'+o+'" alt="MRD Concessionnaire"></p><hr><h2 id="exercices-de-lecture-en-classe" tabindex="-1">Exercices de lecture (en classe) <a class="header-anchor" href="#exercices-de-lecture-en-classe" aria-label="Permalink to &quot;Exercices de lecture (en classe)&quot;">​</a></h2><ol><li>Identifie les <strong>PK</strong> et les <strong>FK</strong>.</li><li>Indique les <strong>relations</strong> et leur cardinalité.</li><li>Quelle table représente une <strong>action / événement</strong> ?</li><li>Où sont stockés : <ul><li>le prix de vente</li><li>le vendeur</li><li>la voiture vendue</li></ul></li><li>Qu&#39;y a-t-il de spécial sur la table vendeur?</li></ol><blockquote><p>Remarquer également les conventions de nomenclature :</p><ul><li>Les <strong>tables</strong> sont nommées au <strong>singulier</strong> (<code>client</code>, <code>vendeur</code>, <code>voiture</code>, <code>achat</code>).</li><li>Les <strong>clés primaires</strong> portent le nom <code>id_&lt;table&gt;</code> (ex. : <code>id_client</code>).</li><li>Les <strong>clés étrangères</strong> reprennent le nom de la PK qu’elles référencent<br> (ex. : <code>achat.id_client</code> → <code>client.id_client</code>).</li><li>Les noms sont en <strong>minuscules</strong>, sans accents ni espaces.</li><li>Les mots sont séparés par un <strong>underscore</strong> (<code>snake_case</code>).</li><li>Les noms décrivent clairement le <strong>rôle</strong> de la colonne.</li></ul></blockquote><div class="my-6 rounded-lg border border-blue-300 bg-blue-50 p-4 text-blue-900"><strong class="block">ℹ️ À faire maintenant</strong><p class="m-0"> Pour mettre ces notions en pratique, passez au <a href="./../../labs/lab02-modelisation" class="font-semibold underline hover:text-blue-700"> Laboratoire 2 — Modélisation </a>. </p></div>',23)])])}const h=n(t,[["render",i]]);export{g as __pageData,h as default};
