import{_ as i,o as r,c as a,ag as n}from"./chunks/framework.CpVu2XTb.js";const m=JSON.parse('{"title":"03 — Update, delete","description":"","frontmatter":{"title":"03 — Update, delete","aside":false},"headers":[],"relativePath":"modules/03-sql-base/03-update-delete.md","filePath":"modules/03-sql-base/03-update-delete.md"}'),t={name:"modules/03-sql-base/03-update-delete.md"};function o(s,e,l,p,d,u){return r(),a("div",null,[...e[0]||(e[0]=[n('<h1 id="_03-—-modification-et-suppression-de-donnees" tabindex="-1">03 — Modification et suppression de données <a class="header-anchor" href="#_03-—-modification-et-suppression-de-donnees" aria-label="Permalink to &quot;03 — Modification et suppression de données&quot;">​</a></h1><h2 id="objectif" tabindex="-1">Objectif <a class="header-anchor" href="#objectif" aria-label="Permalink to &quot;Objectif&quot;">​</a></h2><ul><li>Comprendre <strong>pourquoi</strong> on modifie ou supprime des données (contexte applicatif)</li><li>Modifier des données avec <code>UPDATE ... SET ... WHERE</code></li><li>Supprimer des données avec <code>DELETE ... WHERE</code></li><li>Comprendre les <strong>risques</strong> liés aux opérations destructives</li></ul><blockquote><p>Utiliser la même base de données qui a été importée précédemment.</p></blockquote><hr><h2 id="pourquoi-modifier-et-supprimer" tabindex="-1">Pourquoi modifier et supprimer ? <a class="header-anchor" href="#pourquoi-modifier-et-supprimer" aria-label="Permalink to &quot;Pourquoi modifier et supprimer ?&quot;">​</a></h2><p>Dans une application réelle, on <strong>modifie</strong> des données pour :</p><ul><li>corriger une erreur (ex. : une ville mal saisie)</li><li>mettre à jour un état (ex. : actif / inactif)</li><li>ajuster une règle d’affaires (ex. : capacité d’un événement)</li></ul><p>On <strong>supprime</strong> des données pour :</p><ul><li>retirer une donnée créée par erreur</li><li>nettoyer des données de test</li><li>respecter une loi (loi 25)</li></ul><blockquote><p>En pratique, on supprime rarement.<br> On privilégie souvent un champ <code>actif</code> (suppression logique).</p></blockquote><hr><h2 id="update-—-modifier-des-donnees" tabindex="-1">UPDATE — Modifier des données <a class="header-anchor" href="#update-—-modifier-des-donnees" aria-label="Permalink to &quot;UPDATE — Modifier des données&quot;">​</a></h2><h3 id="syntaxe-generale" tabindex="-1">Syntaxe générale <a class="header-anchor" href="#syntaxe-generale" aria-label="Permalink to &quot;Syntaxe générale&quot;">​</a></h3><p>UPDATE table<br> SET colonne = valeur<br> WHERE condition;</p><blockquote><p>⚠️ Sans <code>WHERE</code>, <strong>toutes les lignes</strong> seront modifiées.</p></blockquote><hr><h3 id="exemple-1-—-corriger-une-erreur-de-lieu" tabindex="-1">Exemple 1 — Corriger une erreur de lieu <a class="header-anchor" href="#exemple-1-—-corriger-une-erreur-de-lieu" aria-label="Permalink to &quot;Exemple 1 — Corriger une erreur de lieu&quot;">​</a></h3><p>Contexte :<br> Le lieu de l’événement avec l’id 4 est incorrect.</p><p>UPDATE evenement<br> SET lieu = &#39;Montréal&#39;<br> WHERE id = 4;</p><hr><h3 id="exemple-2-—-augmenter-la-capacite-d-un-evenement-populaire" tabindex="-1">Exemple 2 — Augmenter la capacité d’un événement populaire <a class="header-anchor" href="#exemple-2-—-augmenter-la-capacite-d-un-evenement-populaire" aria-label="Permalink to &quot;Exemple 2 — Augmenter la capacité d’un événement populaire&quot;">​</a></h3><p>Contexte :<br> Le Salon Open Source (id 16) attire plus de participants que prévu.</p><p>UPDATE evenement<br> SET capacite = capacite + 100<br> WHERE id = 16;</p><hr><h3 id="exemple-3-—-desactiver-un-participant-soft-delete" tabindex="-1">Exemple 3 — Désactiver un participant (soft delete) <a class="header-anchor" href="#exemple-3-—-desactiver-un-participant-soft-delete" aria-label="Permalink to &quot;Exemple 3 — Désactiver un participant (soft delete)&quot;">​</a></h3><p>Contexte :<br> Un participant ne souhaite plus utiliser la plateforme.</p><p>UPDATE participant<br> SET actif = false<br> WHERE courriel = &#39;julie.petit2@example.com&#39;;</p><hr><h3 id="exemple-4-—-modifier-plusieurs-colonnes-a-la-fois" tabindex="-1">Exemple 4 — Modifier plusieurs colonnes à la fois <a class="header-anchor" href="#exemple-4-—-modifier-plusieurs-colonnes-a-la-fois" aria-label="Permalink to &quot;Exemple 4 — Modifier plusieurs colonnes à la fois&quot;">​</a></h3><p>Contexte :<br> On met à jour le nom et la capacité d’un événement.</p><p>UPDATE evenement<br> SET nom = &#39;Conférence Tech 2026 (édition spéciale)&#39;,<br> capacite = 350<br> WHERE id = 1;</p><hr><h3 id="exemple-5-—-mise-a-jour-conditionnelle-plusieurs-lignes" tabindex="-1">Exemple 5 — Mise à jour conditionnelle (plusieurs lignes) <a class="header-anchor" href="#exemple-5-—-mise-a-jour-conditionnelle-plusieurs-lignes" aria-label="Permalink to &quot;Exemple 5 — Mise à jour conditionnelle (plusieurs lignes)&quot;">​</a></h3><p>Contexte :<br> Les événements ayant une capacité trop faible sont annulés.</p><p>UPDATE evenement<br> SET actif = false<br> WHERE capacite &lt; 50;</p><hr><h2 id="update-avec-sous-requete" tabindex="-1">UPDATE avec sous-requête <a class="header-anchor" href="#update-avec-sous-requete" aria-label="Permalink to &quot;UPDATE avec sous-requête&quot;">​</a></h2><h3 id="exemple-6-—-desactiver-les-inscriptions-des-participants-inactifs" tabindex="-1">Exemple 6 — Désactiver les inscriptions des participants inactifs <a class="header-anchor" href="#exemple-6-—-desactiver-les-inscriptions-des-participants-inactifs" aria-label="Permalink to &quot;Exemple 6 — Désactiver les inscriptions des participants inactifs&quot;">​</a></h3><p>Contexte :<br> Lorsqu’un participant devient inactif, ses inscriptions doivent aussi être désactivées.</p><p>UPDATE inscription<br> SET actif = false<br> WHERE participant_id IN (<br> SELECT id<br> FROM participant<br> WHERE actif = false<br> );</p><hr><h2 id="delete-—-supprimer-des-donnees" tabindex="-1">DELETE — Supprimer des données <a class="header-anchor" href="#delete-—-supprimer-des-donnees" aria-label="Permalink to &quot;DELETE — Supprimer des données&quot;">​</a></h2><h3 id="syntaxe-generale-1" tabindex="-1">Syntaxe générale <a class="header-anchor" href="#syntaxe-generale-1" aria-label="Permalink to &quot;Syntaxe générale&quot;">​</a></h3><p>DELETE FROM table<br> WHERE condition;</p><blockquote><p>⚠️ Sans <code>WHERE</code>, <strong>toutes les lignes</strong> seront supprimées.</p></blockquote><hr><h3 id="exemple-7-—-supprimer-une-inscription-creee-par-erreur" tabindex="-1">Exemple 7 — Supprimer une inscription créée par erreur <a class="header-anchor" href="#exemple-7-—-supprimer-une-inscription-creee-par-erreur" aria-label="Permalink to &quot;Exemple 7 — Supprimer une inscription créée par erreur&quot;">​</a></h3><p>Contexte :<br> Une inscription incorrecte doit être retirée.</p><p>DELETE FROM inscription<br> WHERE id = 21;</p><hr><h3 id="exemple-8-—-supprimer-les-inscriptions-d-un-evenement-annule" tabindex="-1">Exemple 8 — Supprimer les inscriptions d’un événement annulé <a class="header-anchor" href="#exemple-8-—-supprimer-les-inscriptions-d-un-evenement-annule" aria-label="Permalink to &quot;Exemple 8 — Supprimer les inscriptions d’un événement annulé&quot;">​</a></h3><p>Contexte :<br> Un événement a été annulé et toutes ses inscriptions doivent être supprimées.</p><p>DELETE FROM inscription<br> WHERE evenement_id = 5;</p><hr><h2 id="⚠️-mise-en-garde-importante" tabindex="-1">⚠️ Mise en garde importante <a class="header-anchor" href="#⚠️-mise-en-garde-importante" aria-label="Permalink to &quot;⚠️ Mise en garde importante&quot;">​</a></h2><p>Avant d’exécuter un <code>UPDATE</code> ou un <code>DELETE</code> :</p><ol><li>Tester la condition avec un <code>SELECT</code></li><li>Vérifier le nombre de lignes affectées</li><li>S’assurer que la condition est précise</li></ol><p>Exemple de bonne pratique :</p><p>SELECT *<br> FROM evenement<br> WHERE capacite &lt; 50;</p><p>➡️ Si le résultat est conforme, <strong>alors seulement</strong> appliquer le <code>UPDATE</code> ou le <code>DELETE</code>.</p><hr><h2 id="a-retenir" tabindex="-1">À retenir <a class="header-anchor" href="#a-retenir" aria-label="Permalink to &quot;À retenir&quot;">​</a></h2><ul><li><code>UPDATE</code> modifie des données existantes</li><li><code>DELETE</code> supprime définitivement des lignes</li><li><code>WHERE</code> est <strong>essentiel</strong></li><li>En contexte applicatif, on préfère souvent la <strong>suppression logique</strong> (<code>actif = false</code>)</li></ul>',64)])])}const E=i(t,[["render",o]]);export{m as __pageData,E as default};
