import{_ as a,o as i,c as n,ag as r}from"./chunks/framework.CpVu2XTb.js";const m=JSON.parse('{"title":"04 — Opérateurs et filtres","description":"","frontmatter":{"title":"04 — Opérateurs et filtres","aside":false},"headers":[],"relativePath":"modules/03-sql-base/04-operateurs.md","filePath":"modules/03-sql-base/04-operateurs.md"}'),t={name:"modules/03-sql-base/04-operateurs.md"};function s(o,e,l,c,p,d){return i(),n("div",null,[...e[0]||(e[0]=[r('<h1 id="_04-—-operateurs-et-filtres-sql" tabindex="-1">04 — Opérateurs et filtres SQL <a class="header-anchor" href="#_04-—-operateurs-et-filtres-sql" aria-label="Permalink to &quot;04 — Opérateurs et filtres SQL&quot;">​</a></h1><h2 id="objectif" tabindex="-1">Objectif <a class="header-anchor" href="#objectif" aria-label="Permalink to &quot;Objectif&quot;">​</a></h2><ul><li>Combiner des conditions avec <code>AND</code>, <code>OR</code>, <code>NOT</code></li><li>Filtrer des données avec <code>LIKE</code>, <code>BETWEEN</code>, <code>IN</code></li><li>Éliminer les doublons avec <code>DISTINCT</code></li><li>Lire et écrire des requêtes plus proches de la réalité applicative</li></ul><hr><h2 id="rappel-—-where" tabindex="-1">Rappel — WHERE <a class="header-anchor" href="#rappel-—-where" aria-label="Permalink to &quot;Rappel — WHERE&quot;">​</a></h2><p>Les opérateurs vus dans ce module sont utilisés <strong>principalement dans la clause <code>WHERE</code></strong> afin de filtrer les lignes retournées ou modifiées.</p><hr><h2 id="and-—-toutes-les-conditions-doivent-etre-vraies" tabindex="-1">AND — Toutes les conditions doivent être vraies <a class="header-anchor" href="#and-—-toutes-les-conditions-doivent-etre-vraies" aria-label="Permalink to &quot;AND — Toutes les conditions doivent être vraies&quot;">​</a></h2><h3 id="principe" tabindex="-1">Principe <a class="header-anchor" href="#principe" aria-label="Permalink to &quot;Principe&quot;">​</a></h3><p><code>AND</code> permet de combiner plusieurs conditions qui doivent <strong>toutes</strong> être respectées.</p><h3 id="exemple-—-evenements-a-paris-avec-grande-capacite" tabindex="-1">Exemple — Événements à Paris avec grande capacité <a class="header-anchor" href="#exemple-—-evenements-a-paris-avec-grande-capacite" aria-label="Permalink to &quot;Exemple — Événements à Paris avec grande capacité&quot;">​</a></h3><p>SELECT *<br> FROM evenement<br> WHERE lieu = &#39;Paris&#39;<br> AND capacite &gt;= 300;</p><hr><h2 id="or-—-au-moins-une-condition-vraie" tabindex="-1">OR — Au moins une condition vraie <a class="header-anchor" href="#or-—-au-moins-une-condition-vraie" aria-label="Permalink to &quot;OR — Au moins une condition vraie&quot;">​</a></h2><h3 id="principe-1" tabindex="-1">Principe <a class="header-anchor" href="#principe-1" aria-label="Permalink to &quot;Principe&quot;">​</a></h3><p><code>OR</code> permet de sélectionner les lignes qui respectent <strong>au moins une</strong> des conditions.</p><h3 id="exemple-—-evenements-a-paris-ou-a-lyon" tabindex="-1">Exemple — Événements à Paris ou à Lyon <a class="header-anchor" href="#exemple-—-evenements-a-paris-ou-a-lyon" aria-label="Permalink to &quot;Exemple — Événements à Paris ou à Lyon&quot;">​</a></h3><p>SELECT *<br> FROM evenement<br> WHERE lieu = &#39;Paris&#39;<br> OR lieu = &#39;Lyon&#39;;</p><hr><h2 id="⚠️-and-or-—-attention-aux-priorites" tabindex="-1">⚠️ AND + OR — Attention aux priorités <a class="header-anchor" href="#⚠️-and-or-—-attention-aux-priorites" aria-label="Permalink to &quot;⚠️ AND + OR — Attention aux priorités&quot;">​</a></h2><h3 id="exemple-—-evenements-a-paris-ou-lyon-mais-seulement-en-2026" tabindex="-1">Exemple — Événements à Paris ou Lyon, mais seulement en 2026 <a class="header-anchor" href="#exemple-—-evenements-a-paris-ou-lyon-mais-seulement-en-2026" aria-label="Permalink to &quot;Exemple — Événements à Paris ou Lyon, mais seulement en 2026&quot;">​</a></h3><p>SELECT *<br> FROM evenement<br> WHERE (lieu = &#39;Paris&#39; OR lieu = &#39;Lyon&#39;)<br> AND date_evenement BETWEEN &#39;2026-01-01&#39; AND &#39;2026-12-31&#39;;</p><p>➡️ Sans les parenthèses, le résultat serait différent.</p><hr><h2 id="not-—-exclure-des-resultats" tabindex="-1">NOT — Exclure des résultats <a class="header-anchor" href="#not-—-exclure-des-resultats" aria-label="Permalink to &quot;NOT — Exclure des résultats&quot;">​</a></h2><h3 id="principe-2" tabindex="-1">Principe <a class="header-anchor" href="#principe-2" aria-label="Permalink to &quot;Principe&quot;">​</a></h3><p><code>NOT</code> inverse une condition.</p><h3 id="exemple-—-participants-qui-ne-sont-pas-actifs" tabindex="-1">Exemple — Participants qui ne sont pas actifs <a class="header-anchor" href="#exemple-—-participants-qui-ne-sont-pas-actifs" aria-label="Permalink to &quot;Exemple — Participants qui ne sont pas actifs&quot;">​</a></h3><p>SELECT *<br> FROM participant<br> WHERE NOT actif;</p><hr><h3 id="exemple-—-evenements-qui-ne-sont-pas-a-paris" tabindex="-1">Exemple — Événements qui ne sont pas à Paris <a class="header-anchor" href="#exemple-—-evenements-qui-ne-sont-pas-a-paris" aria-label="Permalink to &quot;Exemple — Événements qui ne sont pas à Paris&quot;">​</a></h3><p>SELECT *<br> FROM evenement<br> WHERE NOT lieu = &#39;Paris&#39;;</p><hr><h2 id="like-—-recherche-par-motif-texte" tabindex="-1">LIKE — Recherche par motif (texte) <a class="header-anchor" href="#like-—-recherche-par-motif-texte" aria-label="Permalink to &quot;LIKE — Recherche par motif (texte)&quot;">​</a></h2><h3 id="principe-3" tabindex="-1">Principe <a class="header-anchor" href="#principe-3" aria-label="Permalink to &quot;Principe&quot;">​</a></h3><p><code>LIKE</code> permet de rechercher des chaînes de caractères similaires.</p><ul><li><code>%</code> : n’importe quelle suite de caractères</li><li><code>_</code> : un seul caractère</li></ul><h3 id="exemple-—-evenements-contenant-le-mot-conference" tabindex="-1">Exemple — Événements contenant le mot &quot;Conférence&quot; <a class="header-anchor" href="#exemple-—-evenements-contenant-le-mot-conference" aria-label="Permalink to &quot;Exemple — Événements contenant le mot &quot;Conférence&quot;&quot;">​</a></h3><p>SELECT *<br> FROM evenement<br> WHERE nom LIKE &#39;%Conférence%&#39;;</p><hr><h3 id="exemple-—-courriels-se-terminant-par-example-com" tabindex="-1">Exemple — Courriels se terminant par <code>example.com</code> <a class="header-anchor" href="#exemple-—-courriels-se-terminant-par-example-com" aria-label="Permalink to &quot;Exemple — Courriels se terminant par `example.com`&quot;">​</a></h3><p>SELECT *<br> FROM participant<br> WHERE courriel LIKE &#39;%@example.com&#39;;</p><hr><h2 id="between-—-intervalle-de-valeurs" tabindex="-1">BETWEEN — Intervalle de valeurs <a class="header-anchor" href="#between-—-intervalle-de-valeurs" aria-label="Permalink to &quot;BETWEEN — Intervalle de valeurs&quot;">​</a></h2><h3 id="principe-4" tabindex="-1">Principe <a class="header-anchor" href="#principe-4" aria-label="Permalink to &quot;Principe&quot;">​</a></h3><p><code>BETWEEN</code> permet de sélectionner une plage de valeurs <strong>inclusive</strong>.</p><h3 id="exemple-—-evenements-prevus-a-l-ete-2026" tabindex="-1">Exemple — Événements prévus à l’été 2026 <a class="header-anchor" href="#exemple-—-evenements-prevus-a-l-ete-2026" aria-label="Permalink to &quot;Exemple — Événements prévus à l’été 2026&quot;">​</a></h3><p>SELECT *<br> FROM evenement<br> WHERE date_evenement BETWEEN &#39;2026-06-01&#39; AND &#39;2026-08-31&#39;;</p><hr><h3 id="exemple-—-evenements-avec-capacite-moyenne" tabindex="-1">Exemple — Événements avec capacité moyenne <a class="header-anchor" href="#exemple-—-evenements-avec-capacite-moyenne" aria-label="Permalink to &quot;Exemple — Événements avec capacité moyenne&quot;">​</a></h3><p>SELECT *<br> FROM evenement<br> WHERE capacite BETWEEN 100 AND 300;</p><hr><h2 id="in-—-appartenance-a-une-liste" tabindex="-1">IN — Appartenance à une liste <a class="header-anchor" href="#in-—-appartenance-a-une-liste" aria-label="Permalink to &quot;IN — Appartenance à une liste&quot;">​</a></h2><h3 id="principe-5" tabindex="-1">Principe <a class="header-anchor" href="#principe-5" aria-label="Permalink to &quot;Principe&quot;">​</a></h3><p><code>IN</code> permet de tester si une valeur appartient à une liste donnée.</p><h3 id="exemple-—-evenements-dans-certaines-villes" tabindex="-1">Exemple — Événements dans certaines villes <a class="header-anchor" href="#exemple-—-evenements-dans-certaines-villes" aria-label="Permalink to &quot;Exemple — Événements dans certaines villes&quot;">​</a></h3><p>SELECT *<br> FROM evenement<br> WHERE lieu IN (&#39;Paris&#39;, &#39;Lyon&#39;, &#39;Marseille&#39;);</p><hr><h3 id="exemple-—-participants-specifiques-par-id" tabindex="-1">Exemple — Participants spécifiques (par id) <a class="header-anchor" href="#exemple-—-participants-specifiques-par-id" aria-label="Permalink to &quot;Exemple — Participants spécifiques (par id)&quot;">​</a></h3><p>SELECT *<br> FROM participant<br> WHERE id IN (1, 5, 10);</p><hr><h2 id="distinct-—-eliminer-les-doublons" tabindex="-1">DISTINCT — Éliminer les doublons <a class="header-anchor" href="#distinct-—-eliminer-les-doublons" aria-label="Permalink to &quot;DISTINCT — Éliminer les doublons&quot;">​</a></h2><h3 id="principe-6" tabindex="-1">Principe <a class="header-anchor" href="#principe-6" aria-label="Permalink to &quot;Principe&quot;">​</a></h3><p><code>DISTINCT</code> permet de ne retourner que des valeurs <strong>uniques</strong>.</p><h3 id="exemple-—-liste-des-villes-ou-ont-lieu-des-evenements" tabindex="-1">Exemple — Liste des villes où ont lieu des événements <a class="header-anchor" href="#exemple-—-liste-des-villes-ou-ont-lieu-des-evenements" aria-label="Permalink to &quot;Exemple — Liste des villes où ont lieu des événements&quot;">​</a></h3><p>SELECT DISTINCT lieu<br> FROM evenement;</p><hr><h3 id="exemple-—-liste-des-participants-ayant-au-moins-une-inscription" tabindex="-1">Exemple — Liste des participants ayant au moins une inscription <a class="header-anchor" href="#exemple-—-liste-des-participants-ayant-au-moins-une-inscription" aria-label="Permalink to &quot;Exemple — Liste des participants ayant au moins une inscription&quot;">​</a></h3><p>SELECT DISTINCT participant_id<br> FROM inscription;</p><hr><h2 id="combinaisons-realistes" tabindex="-1">Combinaisons réalistes <a class="header-anchor" href="#combinaisons-realistes" aria-label="Permalink to &quot;Combinaisons réalistes&quot;">​</a></h2><h3 id="exemple-—-evenements-a-paris-ou-lyon-hors-ateliers-en-2026" tabindex="-1">Exemple — Événements à Paris ou Lyon, hors ateliers, en 2026 <a class="header-anchor" href="#exemple-—-evenements-a-paris-ou-lyon-hors-ateliers-en-2026" aria-label="Permalink to &quot;Exemple — Événements à Paris ou Lyon, hors ateliers, en 2026&quot;">​</a></h3><p>SELECT *<br> FROM evenement<br> WHERE (lieu IN (&#39;Paris&#39;, &#39;Lyon&#39;))<br> AND NOT nom LIKE &#39;%Atelier%&#39;<br> AND date_evenement BETWEEN &#39;2026-01-01&#39; AND &#39;2026-12-31&#39;;</p><hr><h2 id="a-retenir" tabindex="-1">À retenir <a class="header-anchor" href="#a-retenir" aria-label="Permalink to &quot;À retenir&quot;">​</a></h2><ul><li><code>AND</code> : toutes les conditions</li><li><code>OR</code> : au moins une condition</li><li><code>NOT</code> : exclusion</li><li><code>LIKE</code> : recherche textuelle</li><li><code>BETWEEN</code> : intervalle inclusif</li><li><code>IN</code> : liste de valeurs</li><li><code>DISTINCT</code> : suppression des doublons</li><li>Les parenthèses sont <strong>essentielles</strong> avec <code>AND</code> / <code>OR</code></li></ul>',76)])])}const h=a(t,[["render",s]]);export{m as __pageData,h as default};
