import{_ as r,o as t,c as l,ag as i,j as s}from"./chunks/framework.CpVu2XTb.js";const m=JSON.parse(`{"title":"TP1 — Conception d'une base de données","description":"","frontmatter":{"title":"TP1 — Conception d'une base de données"},"headers":[],"relativePath":"travaux/tp1-creation-bd.md","filePath":"travaux/tp1-creation-bd.md"}`),n={name:"travaux/tp1-creation-bd.md"};function a(o,e,u,d,c,p){return t(),l("div",null,[...e[0]||(e[0]=[i('<h1 id="travail-pratique-1-—-conception-d-une-base-de-donnees-relationnelle-12" tabindex="-1">Travail Pratique #1 — Conception d’une Base de Données Relationnelle (12%) <a class="header-anchor" href="#travail-pratique-1-—-conception-d-une-base-de-donnees-relationnelle-12" aria-label="Permalink to &quot;Travail Pratique #1 — Conception d’une Base de Données Relationnelle (12%)&quot;">​</a></h1><ul><li><strong>Modalité :</strong> Individuel</li><li><strong>Remise :</strong> fichier .zip sur LÉA dans le travail concerné</li><li><strong>Date :</strong> voir le travail concerné sur LÉA</li><li><strong>Retards :</strong> -10% par jour (max 3 jours)</li></ul><hr><h2 id="video-explicative" tabindex="-1">Vidéo explicative <a class="header-anchor" href="#video-explicative" aria-label="Permalink to &quot;Vidéo explicative&quot;">​</a></h2>',4),s("iframe",{width:"560",height:"315",src:"https://www.youtube.com/embed/UxUUIahvHxI?si=is0Yrzs-U2XzNH0q",title:"YouTube video player",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share",referrerpolicy:"strict-origin-when-cross-origin",allowfullscreen:""},null,-1),i('<h2 id="objectif-du-travail-pratique" tabindex="-1">Objectif du travail pratique <a class="header-anchor" href="#objectif-du-travail-pratique" aria-label="Permalink to &quot;Objectif du travail pratique&quot;">​</a></h2><p>À partir d’un <strong>modèle de données textuel</strong>, appliquer les notions de <strong>DDL vues en classe</strong> afin de :</p><ul><li>créer une <strong>base de données PostgreSQL</strong></li><li>définir les <strong>tables</strong> et leurs <strong>colonnes</strong></li><li>définir des <strong>clés primaires</strong> et <strong>clés étrangères</strong></li><li>appliquer des <strong>contraintes simples</strong> (NOT NULL, UNIQUE, CHECK, DEFAULT)</li><li>utiliser des <strong>types ENUM PostgreSQL</strong> lorsque pertinent</li><li>produire une <strong>structure de base de données cohérente et fonctionnelle</strong></li></ul><div class="bg-yellow-50 border border-yellow-200 text-yellow-900 rounded-lg p-4"><strong>Important</strong><br> Ce travail porte uniquement sur la <strong>structure</strong> de la base de données (DDL). Aucune insertion de données ni requête <code>SELECT</code> n’est exigée. </div><hr><h2 id="conventions-de-nommage-attendues" tabindex="-1">Conventions de nommage attendues <a class="header-anchor" href="#conventions-de-nommage-attendues" aria-label="Permalink to &quot;Conventions de nommage attendues&quot;">​</a></h2><p>Les noms exacts des tables et de certaines colonnes ne sont pas imposés.</p><p>Vous devez :</p><ul><li>choisir des noms <strong>pertinents et explicites</strong></li><li>respecter les <strong>conventions vues en classe</strong></li><li>nommer clairement : <ul><li>les clés primaires</li><li>les clés étrangères</li><li>les types ENUM</li></ul></li></ul><hr><h2 id="modele-de-donnees-a-transposer" tabindex="-1">Modèle de données à transposer <a class="header-anchor" href="#modele-de-donnees-a-transposer" aria-label="Permalink to &quot;Modèle de données à transposer&quot;">​</a></h2><p>Chaque bloc représente une <strong>table</strong>.<br> Chaque propriété représente une <strong>colonne</strong>.</p><p>Les éléments entre parenthèses indiquent des <strong>listes de valeurs possibles</strong>.<br><strong>Toutes les colonnes sont obligatoires sauf lorsqu&#39;indiquées nullables.</strong></p><h3 id="adresses" tabindex="-1">Adresses <a class="header-anchor" href="#adresses" aria-label="Permalink to &quot;Adresses&quot;">​</a></h3><ul><li>numero_civique (doit être positif)</li><li>rue</li><li>ville</li><li>province</li><li>code_postal</li><li>pays</li></ul><h3 id="cliniques-veterinaires" tabindex="-1">Cliniques vétérinaires <a class="header-anchor" href="#cliniques-veterinaires" aria-label="Permalink to &quot;Cliniques vétérinaires&quot;">​</a></h3><ul><li>nom</li><li>telephone</li><li>adresse</li><li>date_ouverture</li><li>nb_salles_consultation (doit être positif)</li></ul><h3 id="veterinaires" tabindex="-1">Vétérinaires <a class="header-anchor" href="#veterinaires" aria-label="Permalink to &quot;Vétérinaires&quot;">​</a></h3><ul><li>nom</li><li>prenom</li><li>specialite <em>(generaliste, chirurgie, dentisterie, dermatologie)</em></li><li>telephone</li><li>date_embauche</li><li>adresse</li><li>superviseur <em>(nullable)</em></li></ul><h3 id="affectations-veterinaires" tabindex="-1">Affectations vétérinaires <a class="header-anchor" href="#affectations-veterinaires" aria-label="Permalink to &quot;Affectations vétérinaires&quot;">​</a></h3><p><em>(permet qu’un vétérinaire travaille dans plusieurs cliniques)</em></p><ul><li>clinique</li><li>veterinaire</li><li>date_debut</li><li>date_fin <em>(nullable &amp; date_fin &gt;= date_debut)</em></li></ul><h3 id="proprietaires-d-animaux" tabindex="-1">Propriétaires d’animaux <a class="header-anchor" href="#proprietaires-d-animaux" aria-label="Permalink to &quot;Propriétaires d’animaux&quot;">​</a></h3><ul><li>nom</li><li>prenom</li><li>telephone</li><li>courriel (doit être unique)</li><li>adresse</li></ul><h3 id="animaux" tabindex="-1">Animaux <a class="header-anchor" href="#animaux" aria-label="Permalink to &quot;Animaux&quot;">​</a></h3><ul><li>nom</li><li>espece <em>(chien, chat, lapin, oiseau, autre)</em></li><li>race <em>(nullable)</em></li><li>date_naissance</li><li>poids_kg (doit être positif)</li><li>date_inscription <strong>(par DÉFAUT à la date d&#39;aujourd&#39;hui)</strong></li><li>proprietaire</li><li>clinique</li></ul><h3 id="rendez-vous" tabindex="-1">Rendez-vous <a class="header-anchor" href="#rendez-vous" aria-label="Permalink to &quot;Rendez-vous&quot;">​</a></h3><ul><li>animal</li><li>veterinaire</li><li>clinique</li><li>date_rdv</li><li>heure_rdv</li><li>duree_minutes (doit être positif)</li><li>type_rdv <em>(consultation, vaccination, chirurgie, suivi)</em></li><li>statut <em>(prevu, termine, annule)</em> <strong>Doit être à prevu par DEFAUT</strong></li></ul><h3 id="resume-des-relations" tabindex="-1">Résumé des relations <a class="header-anchor" href="#resume-des-relations" aria-label="Permalink to &quot;Résumé des relations&quot;">​</a></h3><ul><li>Une <strong>clinique</strong> peut avoir plusieurs <strong>vétérinaires</strong> et plusieurs <strong>animaux</strong>.</li><li>Un <strong>vétérinaire</strong> peut travailler dans plusieurs <strong>cliniques</strong> et peut avoir <strong>0 ou 1 superviseur (vétérinaire)</strong> .</li><li>Un <strong>propriétaire</strong> peut posséder plusieurs <strong>animaux</strong>, mais chaque animal a un seul propriétaire.</li><li>Un <strong>animal</strong> est suivi par une seule <strong>clinique</strong>.</li><li>Un <strong>rendez-vous</strong> associe un <strong>animal</strong>, un <strong>vétérinaire</strong> et une <strong>clinique</strong> à une date donnée.</li></ul><hr><h2 id="travail-a-realiser" tabindex="-1">Travail à réaliser <a class="header-anchor" href="#travail-a-realiser" aria-label="Permalink to &quot;Travail à réaliser&quot;">​</a></h2><p>À partir du modèle fourni, écrire les instructions SQL nécessaires pour :</p><ol><li>Créer les <strong>types ENUM PostgreSQL</strong> requis.</li><li>Créer toutes les <strong>tables</strong> du modèle.</li><li>Définir les <strong>clés primaires</strong>.</li><li>Définir les <strong>clés étrangères</strong> et respecter les cardinalités.</li><li>Appliquer les <strong>contraintes simples pertinentes</strong>.</li></ol><p>Le code doit pouvoir être exécuté sur une <strong>base de données vide</strong>.</p><hr><h2 id="directives-techniques" tabindex="-1">Directives techniques <a class="header-anchor" href="#directives-techniques" aria-label="Permalink to &quot;Directives techniques&quot;">​</a></h2><p>Vous devez :</p><ul><li>utiliser uniquement les <strong>instructions vues en classe</strong></li><li>respecter l’<strong>ordre de création</strong> des types et des tables</li><li>limiter l’utilisation de <code>NULL</code> aux cas justifiés</li><li>utiliser : <ul><li><code>NOT NULL</code></li><li><code>UNIQUE</code></li><li><code>CHECK</code></li><li><code>DEFAULT</code></li></ul></li><li>utiliser des <strong>ENUM</strong> pour les listes de valeurs fournies</li></ul><div class="bg-red-50 border border-red-300 text-red-900 rounded-lg p-4"><strong>Important — utilisation des ressources</strong><br> Seules les instructions, méthodes et exemples vus dans le cours (modules 1 et 2) sont autorisés.<br> L’utilisation de solutions générées ou de notions non couvertes au cours entraînera une <strong>pénalité sévère</strong> allant jusqu&#39;à une déclaration de plagiat. </div><hr><h2 id="contenu-de-la-remise" tabindex="-1">Contenu de la remise <a class="header-anchor" href="#contenu-de-la-remise" aria-label="Permalink to &quot;Contenu de la remise&quot;">​</a></h2><p>Nom de l’archive : <strong>PrenomNom_TP1.zip</strong></p><p>Elle doit contenir <strong>exactement deux fichiers SQL</strong> :</p><h3 id="_1-fichier-de-creation-de-la-structure" tabindex="-1">1) Fichier de création de la structure <a class="header-anchor" href="#_1-fichier-de-creation-de-la-structure" aria-label="Permalink to &quot;1) Fichier de création de la structure&quot;">​</a></h3><ul><li>Contient le code SQL que vous avez écrit tel que vu dans les démos et labos</li><li>Inclut : <ul><li>création des types ENUM</li><li>création des tables</li><li>contraintes</li></ul></li></ul><p><strong>Exemple de nom :</strong><br><code>tp1_structure_prenom_nom.sql</code></p><hr><h3 id="_2-export-complet-de-la-base-de-donnees" tabindex="-1">2) Export complet de la base de données <a class="header-anchor" href="#_2-export-complet-de-la-base-de-donnees" aria-label="Permalink to &quot;2) Export complet de la base de données&quot;">​</a></h3><ul><li>Fichier <code>.sql</code> généré avec DBeaver</li><li>Export de la structure complète</li><li>Inclut (cases à cocher) <ul><li>la suppression de la base de données (DROP DATABASE)</li><li>la création de la base de données (CREATE DATABASE)</li></ul></li></ul><p><strong>Exemple de nom :</strong><br><code>tp1_export_prenom_nom.sql</code></p><hr><h2 id="correction" tabindex="-1">Correction <a class="header-anchor" href="#correction" aria-label="Permalink to &quot;Correction&quot;">​</a></h2><p>L’évaluation porte sur :</p><ul><li>la qualité du schéma relationnel</li><li>la pertinence des types et contraintes</li><li>l’exactitude des relations (PK / FK)</li><li>le respect des conventions vues en classe</li><li>la cohérence entre le code écrit et l’export final</li><li>la validité technique des fichiers remis</li></ul><p>La grille détaillée est disponible dans la section <strong>Grilles</strong> du site.</p>',56)])])}const g=r(n,[["render",a]]);export{m as __pageData,g as default};
